  var height = form.get('Height (cm)').getValue();
  var weight = form.get('Weight (kg)').getValue();
  if (height && weight) {
    var bmi = (weight / ((height * height) / 10000)).toFixed(2);
    var status = '';
    if (bmi <= 100) {
      form.get('Body mass index').setValue(bmi);
      if (bmi < 16) {
        status = 'Cachexia';
      } else if (bmi >= 16 && bmi < 17) {
        status = 'Malnutrition of moderate degree (Gomez: 60% to Less than 75% of Standard Weight)';
      } else if (bmi >= 17 && bmi < 18.5) {
        status = 'Malnutrition of mild degree (Gomez: 75% to Less than 90% of Standard Weight)';
      } else if (bmi >= 18.5 && bmi < 25) {
        status = 'Normal';
      } else if (bmi >= 25 && bmi < 30) {
        status = 'Overweight (BMI 25.0-29.9)';
      } else if (bmi >= 30 && bmi < 35) {
        status = 'Obesity';
      } else if (bmi >= 35) {
        status = 'Morbid Obesity';
      }
      form.get('BMI Status').setValue(status);
    }
  } else {
    form.get('BMI Status').setValue('');
    form.get('Body mass index').setValue('');
  }

